eInt(terasp[i].ship) * terasp[i].dataCount);            tat = tat + "<div class='basket_check_items_item'><div class='basket_check_items_item_photo'><img style='max-width:100px;' src='<?=$site_url?>cms/images/" + terasp[i].image + "' alt=''></div><div class='basket_check_items_item_title'><div class='item_name'>" + terasp[i].name + "</div><div class='item_article'>Артикул: <span>45971644</span></div></div><div class='basket_check_items_item_price'>" + terasp[i].price + "</div><div class='basket_check_items_item_price'>" + terasp[i].ship + "</div><div class='basket_check_items_item_counter'><div class='good_basket clearfix'><div class='good_basket_btns clearfix'><div onclick='reduce(" + JSON.stringify(terasp[i].id) + ")' class='good_basket_min'></div><input type='text' class='good_basket_input form_text' value='" + terasp[i].dataCount + "'><div onclick='increase(" + JSON.stringify(terasp[i].id) + ")'  class='good_basket_plus'></div></div></div></div><div class='basket_check_items_item_total'>" + minitotal + "</div><div onclick='dead(" + JSON.stringify(terasp[i].id) + ")' class='item_close'><img src='<?php echo $site_url; ?>images/close-icon.png' alt=''></div></div>";        }        total = (proship + proprice);        $('#adsa').html(tat);        $('#price').html(proprice + " <?php echo $_SESSION['valuvalyut']; ?>");        $('#shipping').html(proship + " <?php echo $_SESSION['valuvalyut']; ?>");        $('#total').html(total + " <?php echo $_SESSION['valuvalyut']; ?>");        var basketCount1Dat = parseInt(localStorage.getItem("basketCount1"));        if (isNaN(basketCount1Dat)) {            document.getElementById('basketMain').innerHTML = "(" + 0 + ")";        } else {            document.getElementById('basketMain').innerHTML = "(" + basketCount1Dat + ")";        }    }    update();    function increase(did) {        var fresh = JSON.parse(localStorage.getItem("data1"));        for (var w = 0; w < fresh.length; w++) {            if (fresh[w].id == did) {                fresh[w].dataCount = parseInt(fresh[w].dataCount) + 1;            }        }        var freshStr = JSON.stringify(fresh);        localStorage.setItem("data1", freshStr);        var basketMain;        basketMain = parseInt(localStorage.getItem("basketCount1"));        localStorage.setItem("basketCount1", basketMain + 1); // add 1 after click        update();    }    function reduce(did) {        var fresh = JSON.parse(localStorage.getItem("data1"));        for (var w = 0; w < fresh.length; w++) {            if (fresh[w].id == did && fresh[w].dataCount > 1) {                fresh[w].dataCount = parseInt(fresh[w].dataCount) - 1;                var basketMain;                basketMain = parseInt(localStorage.getItem("basketCount1"));                localStorage.setItem("basketCount1", basketMain - 1); // tiklananda 1 elave edirik            }        }        var freshStr = JSON.stringify(fresh);        localStorage.setItem("data1", freshStr);        update();    }    function dead(did) {        var fresh = JSON.parse(localStorage.getItem("data1"));        for (var w = 0; w < fresh.length; w++) {            if (fresh[w].id == did) {                if (confirm('Are you sure to remove  \" ' + fresh[w].name + ' \"  from your basket?')) {                    var deleteBasketMain = fresh[w].dataCount;                    fresh.splice(w, 1);                    var basketMain = parseInt(localStorage.getItem("basketCount1"));                    localStorage.setItem("basketCount1", basketMain - deleteBasketMain);                } else { 	//	alert("to be careful");                }            } else {            }        }        var freshStr = JSON.stringify(fresh);        localStorage.setItem("data1", freshStr);        update();    }</script>